import React, {Component} from 'react';
import {Label, FormGroup, Input, Modal, ModalHeader, ModalBody, ModalFooter, Table, Button} from 'reactstrap';
import axios from 'axios';
class App extends Component  {
  state = {
    contactos: [],
    newContactoData: {
      Nombre: '',
      Celular: '',
      Sexo: ''
    },
    newContactoModal: false
  }
  ObtenerContactos(){
    axios.get('https://localhost:44386/api/Contactos').then((response)=>{
      this.setState({
            contactos: response.data
      })
    });
  }

  toggleNuevoContactoModal() {
    this.setState({
      newContactoModal: ! this.state.newContactoModal
    });
  }

  render(){
    let contactos = this.state.contactos.map((contacto)=>{
      return(
        <tr key={contacto.Id}>
          <td>{contacto.id}</td>
          <td>{contacto.Nombre}</td>
          <td> {contacto.Celular}</td>
          <td> {contacto.Sexo}</td>
          <td>
            <Button color="success" size="sm" className="mr-2">Edit</Button>
            <Button color="danger" size="sm">Delete</Button>
          </td>
        </tr>
      )
    });
    return (
      <div className="App container">

        <Table>
          <thead>
            <tr>
              <th>#</th>
              <th>Nombre</th>
              <th>Celular</th>
              <th>Sexo</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {contactos}
          </tbody>
        </Table>
      </div>
    );
  }
}

export default App;
