import React, {Component} from 'react';
import {Redirect} from 'react-router-dom';

class LoginBox extends Component{
  constructor(props){
    super(props);
    this.state = {

    };
  }

  render(){
    <div></div>
  }
}

export default class Ingresar extends Component{
  constructor(props){
    super(props);
    const token = localStorage.getItem("token");
    let ingreso = true;
    if (token==null){
      ingreso = false;
    }
    this.state = {
      nombreUsuario: '',
      password: '',
      ingreso
    }
    //Se setea al método onChange los eventos de onChange de los Input
    this.onChange = this.onChange.bind(this);
    this.submitForm = this.submitForm.bind(this);
  }

  onChange(e){
    this.setState({
      [e.target.name]: e.target.value
    })
  }
  submitForm(e)
  {
    e.preventDefault();
    const {nombreUsuario, password}  = this.state;
    //La llamada al servicio
    if (nombreUsuario==="A" && password==="B"){
      localStorage.setItem("token", "jasdajalkcecklwcljekwej");
      this.setState({
        ingreso: true
      })
    }
}
  render(){
    if (this.state.ingreso){
      return <Redirect to="/contactos"/>
    }
    return (

      <div className="root-container">
        <div className="box-comtroller">
          <div className="controller">
              Ingresar
          </div>
          <div className="controller">
              Registrar
          </div>
        </div>
        <div className="box-container">

        </div>
      </div>

