 constructor(props){
   super(props);
   this.state = {

   };
 }

  submitLogin(e){

  }
 render(){
   return (
     <div className=" inner-container">
       <div className="box-header">
         Login
       </div>
       <div className="box">
         <div className="input-group">
           <label htmlFor="nombreUsuario">Usuario</label>
         <input type="text" name="nombreUsuario" className="login-input" placeholder="Usuario"></input>
         </div>
         <div className ="input-group">
           <label htmlFor="password">Password</label>
           <input type="password" name="password" className="login-input" placeholder="password"></input>
         </div>

           <button type="button" className="login-btn" onClick={this.submitLogin.bind(this)}>Ingresar</button>
       </div>
     </div>
   );
 }
}

