import React, {Component} from 'react';
import {Label, FormGroup, Input, Modal, ModalHeader, ModalBody, ModalFooter, Table, Button} from 'reactstrap';
import axios from 'axios';
class App extends Component  {
  state = {
    contactos: [],
    newContactoData: {
      Nombre: '',
      Celular: '',
      Sexo: ''
    },
    newContactoModal: false
  }
  componentWillMount(){
    axios.get('https://localhost:44386/api/Contactos').then((response)=>{
      this.setState({
            contactos: response.data
      })
    });
  }

  toggleNewContactoModal() {
    this.setState({
      newContactoModal: true
    });
  }

  render(){
    let contactos = this.state.contactos.map((contacto)=>{
      return(
        <tr key={contacto.Id}>
          <td>{contacto.id}</td>
          <td>{contacto.Nombre}</td>
          <td> {contacto.Celular}</td>
          <td> {contacto.Sexo}</td>
          <td>
            <Button color="success" size="sm" className="mr-2">Edit</Button>
            <Button color="danger" size="sm">Delete</Button>
          </td>
        </tr>
      )
    });
    return (
      <div className="App container">
        <Button color="primary" onClick={this.toggleNewContactoModal.bind(this)}>Agregar</Button>
        <Modal isOpen={this.state.newContactoModal} toggle={this.toggleNewContactoModal.bind(this)}>
          <ModalHeader toggle={this.toggleNewContactoModal.bind(this)}>Agregar un Contacto</ModalHeader>
          <ModalBody>
            <FormGroup>
              <Label for="Nombre">Nombre</Label>
              <Input  id="Nombre" value={state.newContactoData.Nombre} onChange={(e)=>{
                let {newFormData} = this.state;
                newFormData.Nombre = e.target.value;
                this.setState({newFormData});
              }}/>
            </FormGroup>
            <FormGroup>
              <Label for="Celular">Celular</Label>
              <Input  id="Celular"/>
            </FormGroup>
            <FormGroup>
              <Label for="Sexo">Sexo</Label>
              <Input  id="Sexo"/>
            </FormGroup>
          </ModalBody>
         <ModalFooter>
           <Button color="primary" onClick={this.toggleNewContactoModal.bind(this)}>Guardar</Button>{' '}
           <Button color="secondary" onClick={this.toggleNewContactoModal.bind(this)}>Cancelar</Button>
         </ModalFooter>
        </Modal>
        <Table>
          <thead>
            <tr>
              <th>#</th>
              <th>Nombre</th>
              <th>Celular</th>
              <th>Sexo</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {contactos}
          </tbody>
        </Table>
      </div>
    );
  }
}

export default App;
