import React, {Component} from 'react'
import {Redirect} from 'react-router-dom'
export default class Ingresar extends Component{
  constructor(props){
    super(props);
    const token = localStorage.getItem("token");
    let ingreso = true;
    if (token==null){
      ingreso = false;
    }
    this.state = {
      nombreUsuario: '',
      password: '',
      ingreso
    }
    //Se setea al método onChange los eventos de onChange de los Input
    this.onChange = this.onChange.bind(this);
    this.submitForm = this.submitForm.bind(this);
  }

  onChange(e){
    this.setState({
      [e.target.name]: e.target.value
    })
  }
  submitForm(e)
  {
    e.preventDefault();
    const {nombreUsuario, password}  = this.state;
    //La llamada al servicio
    if (nombreUsuario==="A" && password==="B"){
      localStorage.setItem("token", "jasdajalkcecklwcljekwej");
      this.setState({
        ingreso: true
      })
    }
}
  render(){
    if (this.state.ingreso){
      return <Redirect to="/contactos"/>
    }
    return (

      <div id="cover-caption">
          <div id="container" class="container">
              <div class="row">
                  <div class="col-sm-10 offset-sm-1 text-center">
                      <h1 class="display-5  my-7">Login</h1>
                      <div class="info-form">

                        <form onSubmit={this.submitForm}>
                            <label htmlFor="nombreUsuario">Usuario: </label>
                          <input className ="my-7" type= "text" placeholder="nombreUsuario" name="nombreUsuario" value={this.state.nombreUsuario} onChange={this.onChange}/>

                          <br/>
                        <label htmlFor="password">Password: </label>
                        <input className="my-7" type= "password" placeholder="password" name="password" value={this.state.password} onChange={this.onChange}/>
                          <br/>
                          <input type="submit"></input>
                        </form>

                    </div>
                  </div>
    </div>
  </div>
</div>

    )
  }
}
