import React, {Component} from 'react'
import {Redirect} from 'react-router-dom'
export default class Ingresar extends Component{
  constructor(props){
    super(props);
    let ingreso = false;
    this.state = {
      nombreUsuario: '',
      password: '',
      ingreso
    }
    //Se setea al método onChange los eventos de onChange de los Input
    this.onChange = this.onChange.bind(this);
    this.submitForm = this.submitForm.bind(this);
  }

  onChange(e){
    this.setState({
      [e.target.name]: e.target.value
    })
  }
  submitForm(e)
  {
    e.preventDefault();
    const {nombreUsuario, password}  = this.state;
    //La llamada al servicio
  }
  render(){
    if (this.state.ingreso){
      return <Redirect to="/contactos"/>
    }
    return (
      <div>
          <h1>Login</h1>
        <form onSubmit={this.submitForm}>
          <input type= "text" placeholder="nombreUsuario" name="nombreUsuario" value={this.state.nombreUsuario} onChange={this.onChange}/>
          <br/>
        <input type= "password" placeholder="password" name="password" value={this.state.password} onChange={this.onChange}/>
          <br/>
          <input type="submit"></input>
        </form>
      </div>
    )
  }
}
